sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
  var accountType = g_form.getValue('account_type');
if (accountType == 'saving') {
        // Show savings-related fields
        g_form.setDisplay('interest_rate', true);
        g_form.setDisplay('min_balance_requirement1', true);
	g_form.setDisplay('initial_deposit_amount', false);
		g_form.setDisplay('overdraft_limit', false);
		}
		else if (accountType =='current') {
        // Show current account-related fields
        g_form.setDisplay('overdraft_limit', true);
         g_form.setDisplay('initial_deposit_amount', true);
        // Hide savings-related fields
        g_form.setDisplay('interest_rate', false);
        g_form.setDisplay('min_balance_requirement1', false);

			}   else {
        // Hide all fields if account type is not specified
        g_form.setDisplay('interest_rate', false);
        g_form.setDisplay('min_balance_requirement1', false);
        g_form.setDisplay('initial_deposit_amount', false);
        g_form.setDisplay('overdraft_limit', false);
    }

   }

   //Type appropriate comment here, and begin script below
   return;
   }
