sys_script_include.script:
var MyAjaxProcessor = Class.create();
MyAjaxProcessor.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getUniqueCities: function () {
        var selectedState = this.getParameter('sysparm_selected_state');
        var uniqueCities = {};

        // Create a GlideRecord to fetch data from the City table
        var cityRecord = new GlideRecord('x_1129750_banking_city');
        cityRecord.addQuery('state', selectedState);
        cityRecord.query();

        // Iterate through the records and store unique city details
        while (cityRecord.next()) {
            var cityKey = cityRecord.getValue('state') + '-' + cityRecord.getValue('city_name');

            if (!uniqueCities[cityKey]) {
                uniqueCities[cityKey] = {
                    branchCode: cityRecord.getValue('branch_code'),
                    pincode: cityRecord.getValue('pincode'),
                    address: cityRecord.getValue('address')
                };
            }
        }

        return JSON.stringify(uniqueCities);
    },

    type: 'MyAjaxProcessor'
});
